class DanArrowButtonV7{static instances=[];constructor(button){this.button=button;this.textContainer=button.querySelector('.dan-arrow-button-v7__text-container');this.textElement=button.querySelector('.dan-arrow-button-v7__text');this.textSwapElement=button.querySelector('.dan-arrow-button-v7__text-swap');this.init()}
init(){if(this.textElement&&this.textSwapElement&&this.textContainer){const textWidth=this.textElement.offsetWidth;const swapWidth=this.textSwapElement.offsetWidth;const textHeight=this.textElement.offsetHeight;const swapHeight=this.textSwapElement.offsetHeight;this.textContainer.style.width=Math.max(textWidth,swapWidth)+'px';this.textContainer.style.height=Math.max(textHeight,swapHeight)+'px';this.textElement.style.position='absolute';this.textSwapElement.style.position='absolute'}}
destroy(){if(this.textElement)this.textElement.style.position='';if(this.textSwapElement)this.textSwapElement.style.position='';if(this.textContainer){this.textContainer.style.width='';this.textContainer.style.height=''}}
static destroyAllInstances(){DanArrowButtonV7.instances.forEach(instance=>instance.destroy());DanArrowButtonV7.instances=[]}}
function dancepad_arrow_button_v7(){DanArrowButtonV7.destroyAllInstances();document.querySelectorAll('.dan-arrow-button-v7').forEach(button=>{button.removeAttribute('data-flickering');const instance=new DanArrowButtonV7(button);DanArrowButtonV7.instances.push(instance)})}