class DanDynamicCopyright{static instances=[];constructor(element){this.element=element;this.yearElement=element.querySelector('.dan-dynamic-copyright__year');this.init()}
init(){this.element.removeAttribute('data-flickering');this.yearElement.textContent=new Date().getFullYear()}
destroy(){}
static destroyAllInstances(){DanDynamicCopyright.instances.forEach((instance)=>instance.destroy());DanDynamicCopyright.instances=[]}}
function dancepad_dynamic_copyright(){DanDynamicCopyright.destroyAllInstances();document.querySelectorAll('.dan-dynamic-copyright').forEach((element)=>{const copyright=new DanDynamicCopyright(element);DanDynamicCopyright.instances.push(copyright)})}