class NextGlitchy{constructor(){this.listElement=document.querySelector('.dan-glitchy__list');this.config=null;if(this.listElement){this.config=this.parseConfig();this.init()}}
static instance=null;static initialize(){if(NextGlitchy.instance){NextGlitchy.instance.destroy()}
NextGlitchy.instance=new NextGlitchy()}
parseConfig(){const splitAttribute=(attr)=>this.listElement.getAttribute(attr)?.split('d4511782')||[];return{playModes:splitAttribute('data-playmodes'),durations:splitAttribute('data-durations'),iterations:splitAttribute('data-iterations'),easings:splitAttribute('data-easings'),starts:splitAttribute('data-starts'),ends:splitAttribute('data-ends'),velocitys:splitAttribute('data-velocitys'),amplitudeXs:splitAttribute('data-amplitudexs'),amplitudeYs:splitAttribute('data-amplitudeys'),counts:splitAttribute('data-counts'),velocitySteps:splitAttribute('data-velocitysteps'),minHeights:splitAttribute('data-minheights'),maxHeights:splitAttribute('data-maxheights'),pulses:splitAttribute('data-pulses'),classes:splitAttribute('data-clases').filter(cls=>cls.trim()!=='')}}
createGlitchConfig(index){return{playMode:this.config.playModes[index],hideOverflow:!0,timing:{duration:parseInt(this.config.durations[index]),iterations:parseInt(this.config.iterations[index]),easing:this.config.easings[index]},glitchTimeSpan:{start:parseFloat(this.config.starts[index]),end:parseFloat(this.config.ends[index])},shake:{velocity:parseFloat(this.config.velocitys[index]),amplitudeX:parseFloat(this.config.amplitudeXs[index]),amplitudeY:parseFloat(this.config.amplitudeYs[index])},slice:{count:parseInt(this.config.counts[index]),velocity:parseInt(this.config.velocitySteps[index]),minHeight:parseFloat(this.config.minHeights[index]),maxHeight:parseFloat(this.config.maxHeights[index]),hueRotate:!0},pulse:{scale:parseInt(this.config.pulses[index])}}}
applyGlitchEffect(className,index){const config=this.createGlitchConfig(index);PowerGlitch.glitch(`.${className}`,config)}
init(){this.config.classes.forEach((className,index)=>{this.applyGlitchEffect(className,index)})}
destroy(){if(typeof PowerGlitch?.destroy==='function'){this.config?.classes.forEach(className=>{PowerGlitch.destroy(`.${className}`)})}
this.listElement=null;this.config=null}}
function dancepad_glitchy(){NextGlitchy.initialize()}