class DanInvertedCorners{static instances=[];constructor(element){Object.assign(this,{element,corners:element.querySelectorAll(".dan-inverted-corner__content-corner"),positions:Array.from({length:element.querySelectorAll(".dan-inverted-corner__content-corner").length},(_,i)=>element.getAttribute(`data-corner-position-${i + 1}`))});this.initialize()}
initialize(){if(!this.corners.length)return;this.setupCorners();this.removeFlickering()}
setupCorners(){this.corners.forEach((corner,index)=>{corner.setAttribute("data-side",this.positions[index]);corner.setAttribute("data-corner",index+1)})}
removeFlickering(){this.element.removeAttribute("data-flickering")}
destroy(){this.corners.forEach((corner)=>{corner.removeAttribute("data-side");corner.removeAttribute("data-corner")});this.element.setAttribute("data-flickering","true")}
static destroyAll(){DanInvertedCorners.instances.forEach((instance)=>instance.destroy());DanInvertedCorners.instances=[]}}
const dancepad_inverted_corner=()=>{DanInvertedCorners.destroyAll();const instances=[];document.querySelectorAll(".dan-inverted-corner").forEach((element)=>{const instance=new DanInvertedCorners(element);instances.push(instance)});DanInvertedCorners.instances=instances}