class NextMaskButton{static instances=[];constructor(element){this.element=element;this.pointerMoveHandler=this.handlePointerMove.bind(this);this.initialize()}
initialize(){this.element.addEventListener("pointermove",this.pointerMoveHandler)}
handlePointerMove({target,x,y}){const targetElement=target.closest(".dan-mask-button");if(targetElement!==this.element)return;const rect=targetElement.getBoundingClientRect();targetElement.style.setProperty("--x",x-rect.left);targetElement.style.setProperty("--y",y-rect.top)}
destroy(){this.element.removeEventListener("pointermove",this.pointerMoveHandler);this.element.style.removeProperty("--x");this.element.style.removeProperty("--y")}
static destroyAll(){NextMaskButton.instances.forEach(instance=>instance.destroy());NextMaskButton.instances=[]}}
const dancepad_mask_button=()=>{NextMaskButton.destroyAll();document.querySelectorAll(".dan-mask-button").forEach(element=>NextMaskButton.instances.push(new NextMaskButton(element)))}