class NextMousefillTitle{static instances=[];constructor(element){this.element=element;Object.assign(this,{duration:parseFloat(element.getAttribute("data-duration")),ease:element.getAttribute("data-easing"),eventListeners:[]});const triggerSelector=element.getAttribute("data-trigger");this.trigger=(!triggerSelector||triggerSelector==="this")?element:document.querySelector(triggerSelector);this.initialize()}
initialize(){this.setupEventListeners()}
handleMouseMove=(event)=>{const mouseX=event.clientX-this.element.getBoundingClientRect().left;const elementWidth=this.element.getBoundingClientRect().width;const fillPercentage=(mouseX/elementWidth)*100;gsap.to(this.element,{backgroundSize:`${fillPercentage}% 100%`,duration:this.duration,ease:this.ease})}
handleMouseLeave=()=>{gsap.to(this.element,{backgroundSize:"0% 100%",duration:this.duration,ease:this.ease})}
setupEventListeners(){this.trigger.addEventListener("mousemove",this.handleMouseMove);this.trigger.addEventListener("mouseleave",this.handleMouseLeave);this.eventListeners.push({element:this.trigger,type:"mousemove",listener:this.handleMouseMove},{element:this.trigger,type:"mouseleave",listener:this.handleMouseLeave})}
destroy(){this.eventListeners.forEach(({element,type,listener})=>element.removeEventListener(type,listener));this.eventListeners=[]}
static destroyAll(){NextMousefillTitle.instances.forEach(instance=>instance.destroy());NextMousefillTitle.instances=[]}}
const dancepad_mousefill_title=()=>{NextMousefillTitle.destroyAll();document.querySelectorAll(".dan-mousefill-title").forEach(element=>NextMousefillTitle.instances.push(new NextMousefillTitle(element)))}