class OverlayShadows{static instances=[];constructor(element){this.element=element;this.shadow=element.querySelector(".dan-overlay-shadows__mask");this.src=element.getAttribute("data-src");this.noiseSrc=element.getAttribute("data-noise-src");this.noise=element.querySelector(".dan-overlay-shadows__noise");this.setMask(this.src);this.setNoise(this.noiseSrc);this.element.removeAttribute("data-flickering")}
setMask(src){this.shadow.style.maskImage=`url('${src}')`;this.shadow.style.webkitMaskImage=`url('${src}')`}
setNoise(src){this.noise.style.backgroundImage=`url('${src}')`}
destroy(){}
static destroyAllInstances(){OverlayShadows.instances.forEach(instance=>instance.destroy());OverlayShadows.instances=[]}}
function dancepad_overlay_shadows(){OverlayShadows.destroyAllInstances();document.querySelectorAll('.dan-overlay-shadows').forEach(element=>{const instance=new OverlayShadows(element);OverlayShadows.instances.push(instance)})}