class NextParallaxHover{constructor(element){this.element=element;this.atroposInstance=null;this.config=this.initConfig();this.init()}
static instances=[];static destroyAll(){NextParallaxHover.instances.forEach(instance=>instance.destroy());NextParallaxHover.instances=[]}
initConfig(){return{activeOffset:this.element.getAttribute('data-offset'),alwaysActive:this.element.getAttribute('data-always-active')==='1',duration:this.element.getAttribute('data-duration'),rotate:this.element.getAttribute('data-rotate')==='1',rotateTouch:this.element.getAttribute('data-rotatetouch')==='1',rotateXMax:this.element.getAttribute('data-rotate-x-max'),rotateYMax:this.element.getAttribute('data-rotate-y-max'),rotateXInvert:this.element.getAttribute('data-rotate-x-invert')==='1',rotateYInvert:this.element.getAttribute('data-rotate-y-invert')==='1',stretchX:this.element.getAttribute('data-stretch-x'),stretchY:this.element.getAttribute('data-stretch-y'),stretchZ:this.element.getAttribute('data-stretch-z'),shadow:this.element.getAttribute('data-shadow')==='1',shadowOffset:this.element.getAttribute('data-shadow-offset'),shadowScale:this.element.getAttribute('data-shadow-scale'),highlight:this.element.getAttribute('data-highlight')==='1'}}
initAtropos(){this.atroposInstance=Atropos({el:this.element,activeOffset:this.config.activeOffset,alwaysActive:this.config.alwaysActive,duration:this.config.duration,rotate:this.config.rotate,rotateTouch:this.config.rotateTouch,rotateXMax:this.config.rotateXMax,rotateYMax:this.config.rotateYMax,rotateXInvert:this.config.rotateXInvert,rotateYInvert:this.config.rotateYInvert,stretchX:this.config.stretchX,stretchY:this.config.stretchY,stretchZ:this.config.stretchZ,commonOrigin:!0,shadow:this.config.shadow,shadowOffset:this.config.shadowOffset,shadowScale:this.config.shadowScale,highlight:this.config.highlight})}
init(){this.initAtropos()}
destroy(){if(this.atroposInstance){this.atroposInstance.destroy()}
this.element=null;this.atroposInstance=null;this.config=null}}
function dancepad_parallax_hover(){NextParallaxHover.destroyAll();document.querySelectorAll('.dan-parallax-hover').forEach(element=>{const instance=new NextParallaxHover(element);NextParallaxHover.instances.push(instance)})}