class NextRippleButtonV2{static instances=[];constructor(element){Object.assign(this,{element,rippleMasks:element.querySelector(".dan-ripple-button-v2__ripple-masks"),topMask:element.querySelector(".dan-ripple-button-v2__ripple-mask--top"),bottomMask:element.querySelector(".dan-ripple-button-v2__ripple-mask--bottom"),resizeObserver:null});this.initialize()}
initialize(){this.adjustMaskWidth();this.setupResizeObserver()}
adjustMaskWidth(){if(this.bottomMask.offsetWidth>this.topMask.offsetWidth){this.rippleMasks.style.width=`${this.bottomMask.offsetWidth}px`}}
setupResizeObserver(){this.resizeObserver=new ResizeObserver(()=>{this.adjustMaskWidth()});this.resizeObserver.observe(this.element)}
destroy(){if(this.resizeObserver){this.resizeObserver.disconnect()}
this.rippleMasks.style.removeProperty('width')}
static destroyAll(){NextRippleButtonV2.instances.forEach(instance=>instance.destroy());NextRippleButtonV2.instances=[]}}
const dancepad_ripple_button_v2=()=>{NextRippleButtonV2.destroyAll();document.querySelectorAll(".dan-ripple-button-v2").forEach(element=>{element.removeAttribute('data-flickering');NextRippleButtonV2.instances.push(new NextRippleButtonV2(element))})}