class NextRippleButtonV3{static instances=[];constructor(element){Object.assign(this,{element,topTitle:element.querySelector(".dan-ripple-button-v3__title--top"),bottomTitle:element.querySelector(".dan-ripple-button-v3__title--bottom"),resizeObserver:null});this.initialize()}
initialize(){this.adjustTitleWidth();this.setupResizeObserver()}
adjustTitleWidth(){if(this.bottomTitle.offsetWidth>this.topTitle.offsetWidth){this.topTitle.style.width=`${this.bottomTitle.offsetWidth}px`}}
setupResizeObserver(){this.resizeObserver=new ResizeObserver(()=>{this.adjustTitleWidth()});this.resizeObserver.observe(this.element)}
destroy(){if(this.resizeObserver){this.resizeObserver.disconnect()}
this.topTitle.style.removeProperty('width')}
static destroyAll(){NextRippleButtonV3.instances.forEach(instance=>instance.destroy());NextRippleButtonV3.instances=[]}}
const dancepad_ripple_button_v3=()=>{NextRippleButtonV3.destroyAll();document.querySelectorAll(".dan-ripple-button-v3").forEach(element=>{element.removeAttribute('data-flickering');NextRippleButtonV3.instances.push(new NextRippleButtonV3(element))})}