class DanToolteam{static instances=[];constructor(img){this.img=img;this.tooltip=img.closest('.dan-toolteam__member').querySelector('.dan-toolteam__tooltip');this.boundHandleMouseMove=this.handleMouseMove.bind(this);this.boundHandleMouseLeave=this.handleMouseLeave.bind(this);this.bindEvents()}
handleMouseMove(e){const rect=e.target.getBoundingClientRect();const mouseX=e.clientX-rect.left-rect.width/2;const rotateValue=(mouseX/rect.width)*45;const translateValue=(mouseX/rect.width)*50;this.tooltip.style.transform=`translateX(calc(-50% + ${translateValue}px)) rotate(${rotateValue}deg) scale(1)`;this.tooltip.style.opacity='1'}
handleMouseLeave(){this.tooltip.style.opacity='0';this.tooltip.style.transform='translateX(-50%) rotate(0deg) scale(var(--tooltip-scale-from))'}
bindEvents(){this.img.addEventListener('mousemove',this.boundHandleMouseMove);this.img.addEventListener('mouseleave',this.boundHandleMouseLeave)}
destroy(){this.img.removeEventListener('mousemove',this.boundHandleMouseMove);this.img.removeEventListener('mouseleave',this.boundHandleMouseLeave)}
static destroyAllInstances(){DanToolteam.instances.forEach((instance)=>instance.destroy());DanToolteam.instances=[]}}
function dancepad_toolteam(){DanToolteam.destroyAllInstances();document.querySelectorAll('.dan-toolteam__member img').forEach(img=>{const instance=new DanToolteam(img);DanToolteam.instances.push(instance)})}